import { Component } from "react";
import { setAuthToken } from "../services/setAuthToken";

export default class Login extends Component{
    constructor(props) {
        super(props);
        this.state = {value: {
            name: "",
            password:""
        }};
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    handleChange(event) {
        this.setState({value: event.target.value});  
    }
    handleSubmit(event) {
        console.log(this.state.value);
        alert('Welcome ' + this.state.value.name +
         ' with password ' + this.state.value.password);
        event.preventDefault();
    }

    handleSubmit = (name, pass) => {
        //reqres registered sample user
        const loginPayload = {
            name: 'manager',
            password: 'manager'
        }

        axios.put("http://localhost:7000/api/auth/authenticate", loginPayload)
          .then(response => {
            //get token from response
            const token  =  response.data.token;

            //set JWT token to local
            localStorage.setItem("token", token);

            //set token to axios common header
            setAuthToken(token);

     //redirect user to home page
            window.location.href = '/'
          })
          .catch(err => console.log(err));
      };

    render() {
        return (
            <form onSubmit={this.handleSubmit}>        
            <label>
                Name: 
                <input type="text" value={this.state.value.name} onChange={this.handleChange} /> 
            </label>
            <br/>
            <label>
                Password: 
                <input type="password" value={this.state.value.password} onChange={this.handleChange} /> 
            </label>
            <input type="submit" value="Submit" />
            </form>
        );
    }
}
