import { useState } from "react";
import { Form } from "react-router-dom";
import { v4 as uuidv4 } from 'uuid';


const ArtistForm = (props) => {
    const [artist, setArtist] = useState({
        name: props.artist ? props.artist.name : '',
        active: props.artist ? props.artist.active : ''
    });

    const [errorMsg, setErrorMsg] = useState('');
    const {name, active} = artist;

    const handleOnSubmit = (event) =>{
        event.preventDefault();
        const values = [name, active];
        let errorMsg = '';

        const allFieldsFilled = values.every((field) => {
            const value = `${field}`.trim();
            return value !== '' && value !== '0';
        });

        if(allFieldsFilled){
            const artist = {
                id: uuidv4(),
                name,
                active
            };
            props.handleOnSubmit(artist);
        }else{
            errorMsg = 'Please fill out all the fields.'
        }
        setErrorMsg(errorMsg);
    };

    const handleInputChange = (event) =>{
        const {name, value} = event.target;
        setArtist((prevState) => ({
          ...prevState,
          [name]: value
        }));
    }

    return(
        <div className="artist-form">
            {errorMsg && <p className="errorMsg">{errorMsg}</p>}
            <Form onSubmit={handleOnSubmit}>
                <Form.Group controlId = "name">
                    <Form.Label>Artist Name</Form.Label>
                    <Form.Control
                        className="input-control"
                        type="text"
                        name="artistname"
                        value={name}
                        placeholder="Enter artist name"
                        onChange={handleInputChange}
                    />
                </Form.Group>
                <Form.Group controlId="active">
                    <Form.Label>Is artist active?</Form.Label>
                    <Form.Control
                        className="input-control"
                        type="text"
                        name="artistactive"
                        value={active}
                        placeholder="Enter artist status"
                        onChange={handleInputChange}
                    />
                </Form.Group>
                <Button variant="primary" type="submit" className="submit-btn">
                    Submit
                </Button>
            </Form>
        </div>
    );
};

export default ArtistForm;
